import React from "react";
import "./card.css";
import Modal from "react-modal";
class CardRow extends React.Component {
  constructor(props) {
    super();
    this.state = {
      isActive: false
    };
  }
  componentWillMount() {
    Modal.setAppElement("body");
  }
  toggleModal = () => {
    this.setState({
      isActive: !this.state.isActive
    });
  };
  render() {
    return (
      <div className="card">
        <img src={this.props.track.image[2]["#text"]} alt="" />
        <h4>{this.props.track.name}</h4>

        <button onClick={this.toggleModal}>
          {this.props.track.artist.name}
        </button>
        <Modal isopen={this.state.isActive}>Hello</Modal>
      </div>
    );
  }
}

export default CardRow;
