import React, { useState, useEffect } from "react";
import "./App.css";
import Chart from "./Chart";
function App() {
  const [country, setCountry] = useState("india");
  const [tracks, setTracks] = useState([]);
  const [state, setState] = useState(0);
  // useEffect(() => {
