import React from "react";
import "./card.css";
import Modal from "react-modal";
class CardRow extends React.Component {
  constructor() {
    super();
    this.state = {
      active: false,
      image: "",
      listerner: "",
      count: "",
      tag: [],
      publish: "",
      similar: []
    };
  }
  componentWillMount() {
    Modal.setAppElement("body");
  }

  toggleModal = () => {
    var data;
    var url =
      "http://ws.audioscrobbler.com/2.0/?method=artist.getInfo&api_key=1860cfefc2bccd102f8437ff38667f0f&mbid=" +
      this.props.track.artist.mbid +
      "&format=json";
    fetch(url)
      .then(function(response) {
        return response.json();
      })
      .then(function(myJson) {
        data = myJson;
      });
    // this.setState({
    //   image: data.artist.image[1]["#text"],
    //   listerner: data.artist.stats.listener,
    //   count: data.artist.stats.playcount,
    //   tag: data.artist.tags,
    //   publish: data.artist.bio.published,
    //   similar: data.artist.similar
    // });
    console.log(data);
    console.log(url);
    this.setState({
      active: !this.state.active
    });
    console.log(this.props.track.name);
  };

  render() {
    return (
      <div className="card">
        <img src={this.props.track.image[2]["#text"]} alt="" />
        <h4>{this.props.track.name}</h4>

        <a onClick={this.toggleModal}>{this.props.track.artist.name}</a>
        <Modal isOpen={this.state.active} onRequestClose={this.toggleModal}>
          <img src={this.state.image} alt="" />

          <button onClick={this.toggleModal}>Close</button>
        </Modal>
      </div>
    );
  }
}

export default CardRow;
